import Foundation
import PopcornKit

class MoviesViewModel: ObservableObject {
    @Published var isLoading = false
    var page = 1
    @Published var hasNextPage = false
    @Published var currentFilter: MovieManager.Filters = .trending {
        didSet { reload() }
    }
    @Published var currentGenre = NetworkManager.Genres.all {
        didSet { reload() }
    }
    @Published var error: Error? = nil
    @Published var movies: [Movie] = []
    
    func reload() {
        movies = []
        page = 1
        loadMovies()
    }
    
    func loadMovies() {
        guard !isLoading else {
            return
        }
        
        isLoading = true
        PopcornKit.loadMovies(page, filterBy: currentFilter, genre: currentGenre) { [unowned self] (movies, error) in
            isLoading = false
//            print(movies, error)
//            print(movies!.toJSONString())
            
            guard let movies = movies else {
                self.error = error
                return
            }
            
            self.movies = (self.movies + movies).uniqued
            self.hasNextPage = !self.movies.isEmpty
            self.page += 1
        }
    }
}


extension Movie {
    static func dummy() -> Self {
        var movie = Movie.dummiesFromJSON()[0]
        movie.actors = .init(JSONArray: movieActorsJSON)
        movie.crew = .init(JSONArray: movieCrewJSON)
        movie.related = .init(JSONArray: movieRelatedJSON)
        return movie
    }
    
    static func dummiesFromJSON() -> [Movie] {
        return .init(JSONArray: moviesJSON)
    }
}

